import React, { ChangeEvent, useCallback, useEffect, useRef, useState } from 'react';
import { Image } from 'antd';
import { StyledForm, StyledImage, StyledInput, StyledBtn } from './styles';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import instance from '../../services/AxiosManager';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../redux/store';
import CenterSpinner from '../../components/CenterSpinner';
import { log } from 'util';
import { getCookies } from '../../utils/cookie';
import { userSlice } from '../../redux/features/userSlice';
import { openNotification } from '../../utils/notification';
import { uploadImage } from '../../api/uploadImage';

// Function

const MyPage: React.FC = () => {
  const user = useSelector((state: RootState) => state.user);
  const [userId, setUserId] = useState('');
  const [userName, setUserName] = useState('');
  const [userImg, setuserImg] = useState();
  const [profilePath, setProfilePath] = useState('');
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { setUser } = userSlice.actions;

  const imgRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (!user.userId || !user.userName) {
      return;
    }
    setUserId(user.userId.toString());
    setUserName(user.userName);
    setProfilePath(user.imgUrl || '');
  }, [user]);

  const onSelectImage = useCallback(() => {
    if (!imgRef.current) return;
    imgRef.current.click();
  }, [imgRef]);

  const onChangeImage = useCallback((e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      uploadImage(e.target.files[0]).then((res) => {
        console.log(res[0]);
        setProfilePath(res[0]);
      });
    }
  }, []);

  const SavePost = useCallback(async () => {
    try {
      const res = await instance.patch(`/api/users/${user.userId}`, {
        userName: userName,
        profileUrl: profilePath,
      });
      const updatedUser = await instance.get('/api/users');

      openNotification('bottom', '변경 성공', '');
      dispatch(setUser({ ...updatedUser.data }));
      navigate(-1);
    } catch (e: any) {
      console.log(e);
    }
  }, [userId, userName, profilePath, navigate]);

  if (!user) {
    return <CenterSpinner />;
  }

  return (
    <div style={{ textAlign: 'center', maxWidth: '800px', margin: 'auto', padding: '0 1em' }}>
      <Image
        onClick={onSelectImage}
        width={200}
        style={{ marginBottom: '1em', cursor: 'pointer' }}
        src={profilePath}
        fallback='data:image/png;base64,'
      />
      <input
        type='file'
        ref={imgRef}
        value={userImg}
        onChange={(e) => onChangeImage(e)}
        style={{ display: 'block', margin: 'auto' }}
      />
      <p>아이디</p>
      <StyledInput
        disabled
        style={{ marginBottom: '1em' }}
        type='text'
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <p>이름</p>
      <StyledInput
        style={{ marginBottom: '1em' }}
        type='text'
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
      />
      <StyledBtn onClick={SavePost}>저장하기</StyledBtn>
    </div>
  );
};

export default MyPage;
